    }
    for(let i = 0; i < posts.length; i++){
        const post = posts[i];
        if(post.action == "BUY"){
            val -= Number(post.quantity);
        }else{
            val += Number(post.quantity);
        }
    }
    return val;
  }
  getBCVal(){
    let val = 0;
