import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import Chart from 'kaktana-react-lightweight-charts'
import './dashboard.css';

import axios from 'axios'
//pass in viewed user, connection status, and id of user who is viewing
class Dashboard extends Component {
  chartRefBC = React.createRef();
  constructor(props){
    super(props);

    this.updatePosts= this.updatePosts.bind(this);

    this.bcws = new WebSocket ("wss://stream.binance.us:9443/ws/btcusdt@trade");
    this.bcws.onmessage = this.bcOnMessage;
    this.state = {
      posts : [],
      bcLineSeries : [{
          data: []
      }],
      bcData: [],
      bcPrice: 0

    };
  }

  componentDidMount(){
    const myChartRef = this.chartRef.current.getContext("2d");
    const user = localStorage.getItem("userID");
    alert(user);
    if (user.length ==0) {
        alert("you are not logged in");
        window.location.href = '/login';
    }
    this.updatePosts();
}

bcOnMessage(event){
    var response = JSON.parse(event.data)
    var utcMiliSec = response.E
    var temp = {time: utcMiliSec, value: response.p}
    var data = this.state.bcData;
    var lineSeries = this.state.bcLineSeries;
    data.push(temp)
    lineSeries[0].data = data
    this.setState({
        bcData: data,
        bcPrice: response.p,
        bcLineSeries: lineSeries
    })

}
updatePosts(){
    const user = localStorage.getItem("userID");


   axios.post("http://localhost:5000/posts/byuser", {user: user})
       .then(function(res){
         console.log(res);
           this.setState({
               posts: res.data.posts
           });
           console.log(this.state.posts);

       }.bind(this)) 
       .catch(err => console.log('Error: ' + err));
}



  render() {

    return (
      <div id = "postDisplay">
          <Chart  lineSeries={this.state.bcLineSeries} autoWidth height={320} />


      </div>
    );
  }
}

export default Dashboard;
